pragma solidity ^0.4.17;

import "./Mortal_BlocKYC.sol";
import "./Storage_BlocKYC.sol";

//change contract name
//is bank method
contract BankBlocKYC is OwnedBlocKYC, MortalBlocKYC {
    address private storageContractAddress;

    function setStorage(address _storageContract) external onlyOwner {
        storageContractAddress = _storageContract;
    }

    function addBank(address _bankAddress) external onlyOwner {
        StorageBlocKYC(storageContractAddress).addBankToBankList(_bankAddress);
    }

    function checkCustomer(address _customerAddress) external view returns (bool, bool, bool, address) {
        require((StorageBlocKYC(storageContractAddress)).isBank(msg.sender));
        return((StorageBlocKYC(storageContractAddress)).getCustomerDetailsFromNetwork(_customerAddress));
    }

    function verifyCustomer(address _customerAddress) external {
        require((StorageBlocKYC(storageContractAddress)).isBank(msg.sender));
        (StorageBlocKYC(storageContractAddress)).verifyCustomer(_customerAddress, msg.sender);
        (StorageBlocKYC(storageContractAddress)).verifyCustomerInNetwork(_customerAddress, msg.sender);
    }

    function flagCustomer(address _customerAddress) external {
        require((StorageBlocKYC(storageContractAddress)).isBank(msg.sender));
        (StorageBlocKYC(storageContractAddress)).flagCustomerInNetwork(_customerAddress, msg.sender);
    }
}
