pragma solidity ^0.4.17;

import "./Mortal.sol";
import "./Storage.sol";


contract Creator is Owned, Mortal, Storage {

    address[] private doctors;
    
    function createHospital(address _hospitalAddress, string _hospitalName) public onlyOwner {
        hospitaltoOwner[_hospitalAddress] = Hospital(_hospitalName, doctors, true);
    }
    
    function getHospital(address _addr) public onlyOwner view returns (string, address[]) {
        Hospital memory h = hospitaltoOwner[_addr];
        return (h.hospitalName, h.doctors);
    }

    function addDoctor(address _doctorAddress) public {
        require(hospitaltoOwner[msg.sender].initialized);
        hospitaltoOwner[msg.sender].doctors.push(_doctorAddress);
        doctorList[_doctorAddress] = true;
    }

    /*Use this to identify identity as a doctor - no state change*/
    function isHospital() public view returns (bool) {
        return hospitaltoOwner[msg.sender].initialized;
    }

    function createTrialForm(bytes32 _trialFormId) public {
        require(doctorList[msg.sender] == (true));
        ownerToTrailFormId[msg.sender][_trialFormId] = true; 
        trialFormIdToOwner[_trialFormId][msg.sender] = true;
    }
    
    function addTrialData(bytes32 trialId, bytes32 trialHash) public {
        require(doctorList[msg.sender] == (true));
        trialIdToData[trialId].push(TrialData(trialHash, msg.sender));
    }

    /*Use this method to confirm identity as doctor - no state change*/
    function isDoctor() public view returns (bool) {
        return doctorList[msg.sender];
    }
}
